import React, { Component } from 'react';
import './Valid.css';

class Valid extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // fields: {},
      // errors: {},
      email: ' ',
      name: ' ',
      emailerr: 'YES',
      nameerr: 'YES',
    };
  }

  handleValidation = () => {
    let formIsValid = true;
      if(typeof this.state.name !== "undefined"){
         if(!this.state.name.match(/^[a-zA-Z]+$/)){
            formIsValid = false;
            this.setState({nameerr: "Letters Only."});
         }        
      }

      //Email
      if(!this.state.email){
         formIsValid = false;
         this.setState({emailerr: "Cannot be empty"});
      }

      if(typeof this.state.email !== "undefined"){
         let lastAtPos = this.state.email.lastIndexOf('@');
         let lastDotPos = this.state.email.lastIndexOf('.');

         if (!(lastAtPos < lastDotPos && lastAtPos > 0 && this.state.email.indexOf('@@') == -1 && lastDotPos > 2 && (this.state.email.length - lastDotPos) > 2)) {
            formIsValid = false;
            this.setState({emailerr: "Email is not valid"});
          }
     }  
     return formIsValid;
 }

  handleChange = (event) => {   
   event.persist();      
   handleValidation();
      this.setState({email: event.target.value});
      console.log(event.target.value);
  }

  render(){
      return (
         <div>
            <label>Email</label>

            <input refs="email" type="text" placeholder="Email" onChange={this.handleChange} value={this.state.email}/>
            <span style={{color: "red"}}>{this.state.emailerr}</span>
            <br></br><br></br>
            <label>Name</label>
            <input type="text" id="fname" name="firstname" placeholder="Your name.."/>
            <span style={{color: "red"}}>{this.state.nameerr}</span>


         </div>
    )
  }
}

export default Valid;
