import React, { Component } from 'react';
import './Valid.css';

class Valid extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // fields: {},
      // errors: {},
      email: '',
      name: '',
      emailerr: '*',
      nameerr: '*',
      emailvalid: false,
      namevalid: false,
    };
  }

  handleValidation = () => {
    console.log("Called" + this.state.email);
    //Name
      if(typeof this.state.name !== "undefined"){
         if(!this.state.name.match(/^[a-zA-Z]+$/)){
          this.setState({nameValid: false});
            this.setState({nameerr: "Letters Only."});
         }
         if(this.state.name.match(/^[a-zA-Z]+$/)){
            this.setState({nameerr: ""});
            this.setState({namevalid: true});
         }        
      }
      //Email
      if(!this.state.email){
        this.setState({emailvalid: false});
        this.setState({emailerr: "Cannot be empty"});
      }

      if(typeof this.state.email !== "undefined"){
         if (!this.state.email.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)) {
          this.setState({emailvalid: false});
            this.setState({emailerr: "Email is not valid"});
          }
          if (this.state.email.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)) {
            this.setState({emailerr: ""});
            this.setState({emailvalid: true});
          }
     }  
 }

  handleChange = (event) => {       
      const val = event.target.id;
      if(val === 'email')
      {
        console.log('Email entered');
        this.setState({email: event.target.value });
      }
      if(val === 'name')
      {
        this.setState({name: event.target.value });
      }
      console.log("current: "+ event.target.value);
      this.handleValidation();
  }




  validateEmail = (event) => {
    console.log("Current value: "+ event.target.value);
    if(!event.target.value){
      this.setState({emailvalid: false});
      this.setState({emailerr: "Cannot be empty"});
    }

    if(typeof event.target.value !== "undefined"){
       if (!event.target.value.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)) {
        this.setState({emailvalid: false});
          this.setState({emailerr: "Email is not valid"});
        }
        if (event.target.value.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)) {
          this.setState({emailerr: ""});
          this.setState({emailvalid: true});
        }
   }  

   console.log("Valid: "+ this.state.emailvalid);
    this.setState({email: event.target.value });
  }


  render(){
      return (
         <div>
            <label>Email</label>

            <input refs="email" type="text" id="email" onChange={this.validateEmail} value={this.state.email}/>
            <span style={{color: "red"}}>{this.state.emailerr}</span>
            <br></br><br></br>
            <label>Name</label>
            <input type="text" name="firstname" id="name" onChange={this.handleChange} value={this.state.name}/>
            <span style={{color: "red"}}>{this.state.nameerr}</span>

            <button>Submit</button>


         </div>
    )
  }
}

export default Valid;
