import React, { Component } from 'react';
import './Valid.css';

class Valid extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // fields: {},
      // errors: {},
      email: '',
      name: '',
      emailerr: '*',
      nameerr: '*',
      emailvalid: false,
      namevalid: false,
    };
  }

  handleChange = (event) => {       
      const val = event.target.id;
      if(val === 'email')
      {
        console.log('Email entered');
        this.setState({email: event.target.value });
      }
      if(val === 'name')
      {
        this.setState({name: event.target.value });
      }
      console.log("current: "+ event.target.value);
      this.handleValidation();
  }

  validateEmail = (event) => {
    //EMAIL
    let isValid = false;
    if(!event.target.value){
      isValid = false;
      this.setState({emailerr: "Cannot be empty"});
    }

    if(typeof event.target.value !== "undefined"){
       if (!event.target.value.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)) {
        this.setState({emailvalid: false});
          this.setState({emailerr: "Email is not valid"});
        }
        if (event.target.value.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)) {
          this.setState({emailerr: ""});
          this.setState({emailvalid: true});
        }
   }  
   console.log("Valid: "+ this.state.emailvalid);
   this.setState({email: event.target.value });
  }


  validateName = (event) => 
  {
    let isValid = false;
    //Name
    console.log("Current value: "+ event.target.value);
    this.setState({name: event.target.value}, () => {
      console.log("in state: " +this.state.name);
    });
    console.log(this.state.name);
    if(typeof this.state.name !== "undefined"){
      if(!event.target.value.match(/^([A-Za-z]+\s)*[A-Za-z]+$/)){
      this.setState({nameerr: "Not valid name."});
      isValid = false;
      }
      if(event.target.value.match(/^([A-Za-z]+\s)*[A-Za-z]+$/)){
        this.setState({nameerr: ""});
        isValid = true;
      }        
    }
    console.log(isValid);
  }

  showValues = (event) => {
    console.log('FINAL OUTPUT');
    console.log('Email: '+this.state.email);
    console.log('Name: '+this.state.name);
  }

  render(){
      return (
         <div>
            <label>Email</label>

            <input refs="email" type="text" id="email" onChange={this.validateEmail} value={this.state.email}/>
            <span style={{color: "red"}}>{this.state.emailerr}</span>
            <br></br><br></br>
            <label>Name</label>
            <input type="text" name="firstname" id="name" onChange={this.validateName} value={this.state.name}/>
            <span style={{color: "red"}}>{this.state.nameerr}</span>

            <button onClick= {this.showValues}>Submit</button>


         </div>
    )
  }
}

export default Valid;
